content.css:
* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: Arial, sans-serif;
  line-height: 1.6;
}

.container {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 0;
  border-bottom: 1px solid #eee;
}

.nav ul {
  display: flex;
  list-style: none;
}

.nav li {
  margin-left: 20px;
}

.nav a {
  text-decoration: none;
  color: #333;
}

.content {
  flex: 1;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  padding: 20px 0;
}

.card {
  flex: 1 1 300px;
  padding: 20px;
  border-radius: 5px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
  background-color: #fff;
}

.card h2 {
  margin-bottom: 10px;
}

.footer {
  padding: 20px 0;
  border-top: 1px solid #eee;
  text-align: center;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .header {
    flex-direction: column;
  }

  .nav ul {
    margin-top: 20px;
  }

  .nav li {
    margin: 0 10px;
  }
}

content.js:
// Add smooth scrolling to navigation links
document.querySelectorAll('nav a').forEach(anchor => {
  anchor.addEventListener('click', function(e) {
    e.preventDefault();

    // For a real app, this would scroll to the target section
    console.log('Navigating to:', this.getAttribute('href'));

    // Simulate a page change
    document.querySelectorAll('.card').forEach(card => {
      card.classList.add('animate');
    });
  });
});

// Add a class to cards when they come into view
const cards = document.querySelectorAll('.card');

// In a real app, this would use IntersectionObserver
function animateCards() {
  cards.forEach((card, index) => {
    setTimeout(() => {
      card.classList.add('visible');
    }, index * 200);
  });
}

// Call the animation function when the page loads
window.addEventListener('load', animateCards);
